// To create the action{what to happen and what data to be passed} regarding the user 

import { UserActionTypes } from "./user.types";

export const setCurrentUser = user =>(
    {
        type: UserActionTypes.SET_CURRENT_USER,
        payload: user
    }
)

export const googleSignInStart =() =>({
    type: UserActionTypes.GOOGLE_SIGN_IN_START
})

export const googleSignInSuccess = user =>({
    type: UserActionTypes.GOOGLE_SIGN_IN_SUCCESS,
    payload: user
})

export const googleSignInFailure = error =>({
    type: UserActionTypes.GOOGLE_SIGN_IN_FAILURE,
    payload: error
})

export const emailSignInStart =() =>({
    type: UserActionTypes.EMAI_SIGN_IN_START
})

export const emailSignInSuccess = user =>({
    type: UserActionTypes.EMAI_SIGN_IN_SUCCESS,
    payload: user
})

export const emailSignInFailure = error =>({
    type: UserActionTypes.EMAI_SIGN_IN_FAILURE,
    payload: error
})
