// import CartActionTypes from "./cart.types"

const INITIAL_STATE={
    hidden: true
}

const CardReducer =( state =INITIAL_STATE,action) =>{
    switch(action.type){
        case 'TOOGLE_CART_HIDDEN':
            return{
                ...state,
                hidden: !state.hidden
            }
            default:
                return state
    }
}

export default CardReducer
